mapper.select:
SELECT * FROM insurance_qna
WHERE product_id = #{productId}
ORDER BY qna_id DESC

mapper.update:
UPDATE insurance_qna
SET answer = #{answer}
WHERE qna_id = #{qnaId}

UPDATE insurance_qna
SET species = #{species},
    question = #{question}
WHERE qna_id = #{qnaId}

mapper.select:
SELECT * FROM insurance_qna
WHERE product_id = #{productId}
ORDER BY qna_id DESC
LIMIT #{pageSize} OFFSET #{offset}
