  #{title}, #{content}, #{lostLocation}, #{lostTime}, #{contactPhone}, #{userId}, NOW(), NOW()
)

mapper.update:
UPDATE lost_animals
SET comment_count = comment_count + 1
WHERE post_id = #{postId}

UPDATE lost_animals
SET comment_count = comment_count - 1
WHERE post_id = #{postId}
