mapper.select:
SELECT * FROM parttime_job_comment
WHERE job_id = #{jobId}
ORDER BY created_at ASC

SELECT * FROM parttime_job_comment
ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE parttime_job_comment
SET content = #{content}
WHERE comment_id = #{commentId}

mapper.select:
SELECT *
FROM parttime_job_comment
WHERE job_id = #{jobId}
